<script>
    const testimonials = document.querySelectorAll(".testimonial span:not(.arrow)");
    const prevBtn = document.querySelector(".testimonial .prev");
    const nextBtn = document.querySelector(".testimonial .next");
    let index = 0;
    let intervalTime = 5000;
    let sliderInterval;

    function showTestimonial(i) {
        testimonials.forEach((t, idx) => {
            t.classList.toggle("active", idx === i);
        });
    }

    function nextSlide() {
        index = (index + 1) % testimonials.length;
        showTestimonial(index);
    }

    function prevSlide() {
        index = (index - 1 + testimonials.length) % testimonials.length;
        showTestimonial(index);
    }

    function resetInterval() {
        clearInterval(sliderInterval);
        sliderInterval = setInterval(nextSlide, intervalTime);
    }

    nextBtn.addEventListener("click", () => {
        nextSlide();
        resetInterval();
    });
    prevBtn.addEventListener("click", () => {
        prevSlide();
        resetInterval();
    });

    sliderInterval = setInterval(nextSlide, intervalTime);

    function checkForm() {
        oForm = document.FCorporateAccount;

        if (oForm.first_name.value == "") {
            alert("Please enter your first name.");
            oForm.first_name.focus();
            return false;
        }

        if (oForm.last_name.value == "") {
            alert("Please enter your last name.");
            oForm.last_name.focus();
            return false;
        }

        if (oForm.company.value == "") {
            alert("Please enter your company name.");
            oForm.company.focus();
            return false;
        }

        if (oForm.phone.value == "") {
            alert("Please enter your phone number.");
            oForm.phone.focus();
            return false;
        }

        rePhoneNumber = new RegExp(/^([0-9])+$/);

        if (!rePhoneNumber.test(oForm.phone.value)) {
            alert("Phone number is incorrect.");
            oForm.phone.focus();
            return false;
        }

        if (oForm.email.value == "") {
            alert("Please enter your email address.");
            oForm.email.focus();
            return false;
        }

        if (!checkEmail2(oForm.email.value)) {
            alert("Your email address is incorrect.");
            oForm.email.focus();
            return false;
        }

        jQuery('.capcha_validate').click();

    }

    function checkEmail2(email) {
        emailRegExp = /^[_a-zA-Z0-9-]+(\.[_a-zA-Z0-9-]+)*@[a-zA-Z0-9-]+(\.[a-zA-Z0-9-]+)*(\.([a-zA-Z]){2,4})$/;
        if (emailRegExp.test(email)) {
            return true;
        } else {
            return false;
        }
    }

    function sendForm(e) {
        jQuery.ajax({
            data: ({
                email: oForm.email.value,
                gcapcha: e
            }),
            type: "POST",
            dataType: "html",
            async: true,
            url: '/index.php?option=com_contact&task=check_company_discount',
            success: function (data) {
                if (data == 'exist') {
                    alert("<?php echo $company_already_exist; ?>");
                    return false;
                } else {
                    oForm.submit();
                }
            }
        })
    }

    var element = document.querySelector(".alert.alert-danger.alert-dismissible");
    if (element) {
        oForm = document.FCorporateAccount;
        oForm.email.style = "border: 1px solid red;";
    }
</script>
