global $database;
$database = new database($mosConfig_host, $mosConfig_user, $mosConfig_password, $mosConfig_db, $mosConfig_dbprefix);

$sql = "SELECT 
  o.order_id as 'Order #', 
  a.api_order_id as 'Shopify ID', 
  `s`.`order_status_name` as 'Current Order Status', 
  CASE WHEN (
    date_format(
      str_to_date(o.ddate, '%d-%m-%Y'), 
      '%d-%m-%Y'
    )!= ''
  ) then (
    date_format(
      str_to_date(o.ddate, '%d-%m-%Y'), 
      '%d-%m-%Y'
    )
  ) else (
    date_format(
      str_to_date(o.ddate, '%Y-%m-%d'), 
      '%d-%m-%Y'
    )
  ) END as 'Delivery Date', 
`ou`.`user_email` as 'Email Address', 
  IF(
    sc.carrier_code != '', 
    sc.carrier_code, 
    IF(v.name != '', v.name, 'Local')
  ) as Currier, 
  IF(
    sc.tracking_number != '', sc.tracking_number, 
    d.pin
  ) as `Tracking ID`, 
  IF(
    sc.tracking_url != '', sc.tracking_url, 
    ''
  ) as `Tracking Url` ,
  max(i.image_link) as `Image Link`
FROM 
  jos_vm_orders as o 
  inner join jos_vm_api2_orders as a on a.order_id = o.order_id 
  left join `jos_vm_orders_deliveries` as d on d.order_id = o.order_id 
  left join jos_vm_deliveries as v on v.id = d.delivery_type 
  left join tbl_shipstation_orders_couriers as sc on sc.order_id = d.order_id 
  left join jos_vm_order_status s on o.order_status = s.order_status_code 
  left join jos_vm_order_history as h on h.order_id = o.order_id
  left join jos_vm_order_history_images as i on i.history_id = h.order_status_history_id
  LEFT JOIN jos_vm_order_user_info AS ou ON ou.order_id = o.order_id 
WHERE 
  o.order_id IN (".implode(',', $orders).")  
group by 
  o.order_id limit $start, $limit";



$database->setQuery( $sql );
if(!$database->query()) {
    echo $database->stderr( true );
    return;
}

if ($database->getNumRows() == 0) {
    die('no orders');
}
$j = 0;

$inserts_csv = $database->loadAssocList();

header("Content-type: text/csv");
header("Content-Disposition: attachment; filename=partner_orders.csv");
header("Pragma: no-cache");
header("Expires: 0");


$handle = fopen('php://output', 'w');
$headers = array('Order #', 'Shopify ID', 'Current Order Status','Delivery Date','Email Address','Currier', 'Tracking ID', 'Tracking Url','Proof of Delivery image');
fputcsv($handle, $headers);

foreach ($inserts_csv as $v) {
    set_time_limit(60);
    $s3ImageLink = '';
    if($v['Image Link']!='') {

        $historyImageString = imageToBase64(trim(str_replace(" ","%20",$v['Image Link']),'/'));

        $s3ImageLink =  $historyImageString ? $ResizeAndSave->resizeHistoryImageAndSave($historyImageString) : '';
    }
    $row = array(
        $v['Order #'],
        $v['Shopify ID'],
        $v['Current Order Status'],
        $v['Delivery Date'],
        $v['Email Address'],
        $v['Currier'],
        $v['Tracking ID'],
        $v['Tracking Url'],
        $s3ImageLink
    );
    fputcsv($handle, $row);
}
fclose($handle);
unset($inserts_csv);

